import React, { useContext, useEffect } from "react";
import { productsContext } from "../../contexts/ProductContext";
import { Card, CardActions, CardContent, CardMedia, CircularProgress, Grid, Link, Typography } from "@mui/material";
import { changeProductCount } from "../../contexts/ProductContext";
import "../../assets/css/Cart.css";

const Cart = ({item}) => {
  const { getCart, cart, changeProductCount } = useContext(productsContext);

  useEffect(() => {
    getCart();
  }, []);

  return (
    <Grid item xs={3}>
    <Card>

        
      <CardMedia
        component="img"
        height="auto"
        image={item.image[0]}
        alt={item.title}
        />
      <CardContent>
        <Typography variant="body1" color="text.secondary" align="center">
          {item.descriptionSec}
        </Typography>
      </CardContent>

    </Card>
  </Grid>
  );
};

export default Cart;
