import React, { useContext, useEffect } from "react";
import { productsContext } from "../../contexts/ProductContext";
import { Card, CardContent, CardMedia, CircularProgress, Grid, Link, Typography } from "@mui/material";
import { changeProductCount } from "../../contexts/ProductContext";
import "../../assets/css/Cart.css";

const Cart = () => {
  const { getCart, cart, changeProductCount } = useContext(productsContext);

  useEffect(() => {
    getCart();
  }, []);

  return (
    <Grid item xs={3}>
    <Card>
      <Link to={`/product/${item.id}`}>
        
      <CardMedia
        component="img"
        height="auto"
        image={item.image[0]}
        alt={item.title}
        />
      <CardContent>
        <Typography variant="body1" color="text.secondary" align="center">
          {item.descriptionSec}
        </Typography>
      </CardContent>
        </Link>
      <CardActions disableSpacing sx={{ justifyContent: "space-around" }}>
        <FavoriteIcon
          aria-label="share"
          onClick={() => addProductToCart(item)}
          color={checkProductInCart(item.id) ? "error" : "black"}
        >
          <AddShoppingCartIcon />
        </FavoriteIcon>
        <Link to={`edit/${item.id}`} >
          <Button onClick={handleAdd} ><img src="https://www.freeiconspng.com/uploads/edit-icon-orange-pencil-0.png"  width= '25px' /></Button>
        </Link>
      </CardActions>
    </Card>
  </Grid>
  );
};

export default Cart;
